package Assignment;

public class Lab3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Integer Types
		
		byte myByte = 100; // 8-bit signed two's complement integer
        short myShort = 300; // 16-bit signed two's complement integer
        int myInt = 200; // 32-bit signed two's complement integer
        long myLong = 9000L; // 64-bit signed two's complement integer (note the 'L' suffix)

        System.out.println("--- Primitive Integer Types ---");
        System.out.println("Byte: " + myByte);
        System.out.println("Short: " + myShort);
        System.out.println("Int: " + myInt);
        System.out.println("Long: " + myLong);
        System.out.println();
	}

}
